import{_ as r,a as d,b as n,c as t}from"./philippe.7efd2418.js";import{S as o}from"./SpeakerLargeItem.0823ef4a.js";import{o as l,c,a as i,b as a,h as s,F as m,d as e}from"./index.e8b980fe.js";const u=e("img",{id:"roberto-martinez",class:"img border-radius-lg w-100 position-relative z-index-2",src:r,alt:""},null,-1),p=e("div",{class:"colored-shadow",style:{"background-image":'url("./assets/img/people/roberto.png")'}},null,-1),g=e("p",null," O Dr. Roberto Martinez-Maldonado recebeu o grau de Doutor em Tecnologias da Informa\xE7\xE3o pela Universidade de Sydney em 2014. Atualmente, ele \xE9 Professor S\xEAnior de An\xE1lise de Aprendizado e Intera\xE7\xE3o Humano-Computador na Faculdade de Tecnologia da Informa\xE7\xE3o e Coordenador do Centro de An\xE1lise de Aprendizado da Universidade de Monash, Melbourne, VIC, Austr\xE1lia. Ele \xE9 coautor de in\xFAmeros artigos de pesquisa. Ele \xE9 o primeiro australiano/mexicano a receber a Bolsa de Pesquisa da Jacobs Foundation, sediada na Su\xED\xE7a (2021\u201323), por sua pesquisa pioneira em IA Centrada no Ser Humano na Educa\xE7\xE3o. Sua pesquisa se concentra em avan\xE7ar na compreens\xE3o de quest\xF5es s\xF3cio-t\xE9cnicas relacionadas ao uso de intelig\xEAncia artificial na educa\xE7\xE3o, promovendo o uso de metodologias centradas no ser humano para criar interfaces de an\xE1lise de aprendizado com integridade e aprimorar espa\xE7os aut\xEAnticos de aprendizado com capacidades de computa\xE7\xE3o de dados multimodais intensivos. Ele foi Presidente do Programa da Confer\xEAncia Internacional de An\xE1lise de Aprendizado e Conhecimento e da Confer\xEAncia Internacional de Intelig\xEAncia Artificial em Educa\xE7\xE3o, e \xE9 um Associado Regular do subcomit\xEA de Aprendizado e Educa\xE7\xE3o da Confer\xEAncia SIGCHI sobre Fatores Humanos em Sistemas Computacionais. Ele \xE9 Editor Associado do International Journal of Artificial Intelligence in Education. ",-1),b=e("img",{id:"marisol-wong",class:"img border-radius-lg w-100 position-relative z-index-2",src:d,alt:""},null,-1),C=e("div",{class:"colored-shadow",style:{"background-image":'url("./assets/img/people/marisol2.png")'}},null,-1),f=e("p",null," A Dra. Marisol Wong-Villacres \xE9 Professora Associada no Programa de Ci\xEAncia da Computa\xE7\xE3o da Escuela Superior Polit\xE9cnica del Litoral, em Guayaquil, Equador. Em 2021, ela obteve seu doutorado em Computa\xE7\xE3o Centrada no Ser Humano no Instituto de Tecnologia da Ge\xF3rgia, em Atlanta, Estados Unidos, e em 2011, obteve um mestrado em Intera\xE7\xE3o Humano-Computador na Universidade de Indiana, Bloomington. Marisol investiga como teorias das ci\xEAncias sociais, culturais e da aprendizagem podem informar o co-design baseado em pontos fortes de tecnologias com grupos vulner\xE1veis, como fam\xEDlias imigrantes. Contrariamente \xE0 abordagem tradicional de projetar com base em necessidades, que tenta corrigir as defici\xEAncias das popula\xE7\xF5es vulner\xE1veis, o design baseado em pontos fortes que Marisol explora busca reconhecer, aproveitar e aprimorar o conhecimento e as capacidades desses grupos. Marisol foi bolsista Fulbright e bolsista do programa Schlumberger Faculty for the Future. Ela tamb\xE9m \xE9 membro do Comit\xEA Latino-Americano da ACM SIGCHI, \xE9 regularmente Presidente Associada do subcomit\xEA de Computa\xE7\xE3o Cr\xEDtica para o CHI, editora associada do ACM Journal on Computing and Sustainable Societies, Presidente de Confer\xEAncia do CLIHC 2023 e Presidente de Trabalhos do CSCW 2024. ",-1),h=e("img",{id:"claudia-lopez",class:"img border-radius-lg w-100 position-relative z-index-2",src:n,alt:""},null,-1),_=e("div",{class:"colored-shadow",style:{"background-image":'url("./assets/img/people/claudia.png")'}},null,-1),v=e("p",null," Claudia L\xF3pez Moncada trabalha como acad\xEAmica no Departamento de Ci\xEAncia da Computa\xE7\xE3o da Universidade T\xE9cnica Federico Santa Mar\xEDa (UTFSM) em Valpara\xEDso, Chile. Ela tamb\xE9m \xE9 pesquisadora no Centro Nacional de Intelig\xEAncia Artificial (CENIA) e no N\xFAcleo Mil\xEAnio de Pesquisa do Futuro da Intelig\xEAncia Artificial (FAIR) no mesmo pa\xEDs. Obteve seu doutorado em Ci\xEAncias da Informa\xE7\xE3o e Tecnologia pela Universidade de Pittsburgh. Seu trabalho se concentra na intera\xE7\xE3o humano-computador (HCI) e na intelig\xEAncia artificial centrada nas pessoas. Claudia \xE9 co-diretora do Laborat\xF3rio de Experi\xEAncia do Usu\xE1rio em UTFSM e participa ativamente da comunidade de HCI na Am\xE9rica Latina e de iniciativas para aumentar a participa\xE7\xE3o das mulheres na computa\xE7\xE3o. ",-1),I=e("img",{id:"philippe-palanque",class:"img border-radius-lg w-100 position-relative z-index-2",src:t,alt:""},null,-1),A=e("div",{class:"colored-shadow",style:{"background-image":'url("./assets/img/people/philippe.png")'}},null,-1),S=e("p",null," Philippe Palanque \xE9 professor de Ci\xEAncia da Computa\xE7\xE3o na Universidade de Toulouse 3 e \xE9 chefe do departamento de Confiabilidade de Software e Sistemas no IRIT (Laborat\xF3rio de Pesquisa em Ci\xEAncias da Computa\xE7\xE3o de Toulouse). Por muitos anos, ele esteve envolvido em projetos de pesquisa na \xE1rea de Sistemas de Comando e Controle em m\xFAltiplos dom\xEDnios, incluindo drones, interfaces multimodais para cockpits militares, sistemas de segmento terrestre em salas de controle de sat\xE9lites, esta\xE7\xF5es de controle de tr\xE1fego a\xE9reo, seguran\xE7a de voo para plataformas de lan\xE7amento e muito mais. Na avia\xE7\xE3o civil, trabalhou em quest\xF5es de especifica\xE7\xE3o e certifica\xE7\xE3o de novos cockpits interativos (que precisam estar em conformidade com a especifica\xE7\xE3o padr\xE3o ARINC 661) de grandes aeronaves, como o Airbus A350, com um foco recente em intera\xE7\xF5es multitouch. Em todos esses dom\xEDnios, a modelagem conjunta do comportamento do sistema interativo e das tarefas do usu\xE1rio foi um aspecto-chave para avaliar e garantir a compatibilidade entre o trabalho dos operadores e as ferramentas fornecidas para realizar esse trabalho. Ele editou e coeditou mais de vinte livros ou atas de confer\xEAncias e coautorou mais de 200 publica\xE7\xF5es arbitradas em confer\xEAncias e revistas internacionais. Ele \xE9 membro da Academia CHI da ACM SIGCHI e presidente da Assembleia T\xE9cnica da IFIP. ",-1),F={__name:"Speakers",setup(E){return(M,P)=>(l(),c(m,null,[i(o,null,{image:a(()=>[u]),"image-shadow":a(()=>[p]),name:a(()=>[s(" Dr. Roberto Martinez-Maldonado ")]),default:a(()=>[g]),_:1}),i(o,null,{image:a(()=>[b]),"image-shadow":a(()=>[C]),name:a(()=>[s(" Dr. Marisol Wong-Villacres ")]),default:a(()=>[f]),_:1}),i(o,null,{image:a(()=>[h]),"image-shadow":a(()=>[_]),name:a(()=>[s(" Dr. Claudia L\xF3pez Moncada ")]),default:a(()=>[v]),_:1}),i(o,null,{image:a(()=>[I]),"image-shadow":a(()=>[A]),name:a(()=>[s(" Dr. Philippe Palanque ")]),default:a(()=>[S]),_:1})],64))}};export{F as default};
